            Title: new FormControl(),
            Sub_title: new FormControl(),
            description: new FormControl(),
            author: new FormControl(),
        });
    }
    ngOnInit() {
    }
    async choosepic() {
        const image = await Camera.pickImages({
            quality: 100,
            height: 300,
